				raw.type = 'array'
				break
			}
			case 'boolean': {
				raw.type = 'boolean'
				break
			}
			case 'name': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Title', name: 'title', type: 'text' },
					{ label: 'First', name: 'first', type: 'text' },
					{ label: 'Midle', name: 'midle', type: 'text' },
					{ label: 'Last', name: 'last', type: 'text' }
				]
				break
			}
			case 'link': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Label', name: 'label', type: 'text' },
					{ label: 'URL', name: 'url', type: 'url' }
				]
				break
			}
			case 'signature': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Base30', name: 'base30', type: 'text' },
					{ label: 'SVG', name: 'svg', type: 'text' }
				]
				break
			}
			case 'phone': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Area', name: 'area', type: 'text' },
					{ label: 'Number', name: 'number', type: 'text' },
					{ label: 'Full', name: 'full', type: 'text' },
					{ label: 'Formatted', name: 'formatted', type: 'text' }
				]
				break
			}
			case 'address': {
				raw.type = 'collection'
				raw.spec =  [
					{ label: 'Longitude', name: 'longitude', type: 'text' },
					{ label: 'Latitude', name: 'latitude', type: 'text' },
					{ label: 'Zip', name: 'zip', type: 'text' },
					{ label: 'State', name: 'state', type: 'text' },
					{ label: 'City', name: 'city', type: 'text' },
					{ label: 'Street', name: 'street', type: 'text' },
					{ label: 'Street2', name: 'street2', type: 'text' },
					{ label: 'Country', name: 'country', type: 'text' }
				]
				break
			}
			case 'email': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Label', name: 'label', type: 'text' },
					{ label: 'Email', name: 'email', type: 'email' }
				]
				break
			}
			case 'image':
			case 'file': {
				raw.type = 'collection'
				raw.spec =  TYPE_FILE
				break
			}
			case 'timer': {
				raw.type = 'collection'
				raw.spec = [
					{ label: 'Total time', name: 'total_time', type: 'number' },
					{ label: 'Times', name: 'times', type: 'array', spec: TYPE_TIMES }
				]
				break
			}
			case 'connection': {
				raw.type = 'array'
				raw.spec = [
					{ label: 'ID', name: 'id', type: 'text' },
					{ label: 'Identifier', name: 'identifier', type: 'text' }
				]
				break
			}
			case 'date_time': {
				raw.type = 'collection'
				raw.spec = TYPE_DATE_CPL
				break
			}
		}
		if (!mode) {
			mode = 'both'
		}
		if (mode === 'both' || mode === 'formatted') {
			intf.push(formated)
		}
		if (mode === 'both' || mode === 'raw') {
			intf.push(raw)
		}
	}) 
	return intf
}

