function wrapMultipleChoiceRawInArray(input, fields) {
	/* 	converts a single value of type text to an array in the output of most modules
		Only for parameters that are "type": "multiple_choice" in the Knack API
		"input" is the response from the module's API ("type": "multiple_choice")
		"fields" can be response from Get Fields API or metadata.expect from Make module or collection of parameters from Watch Module
		metadata.expect can have "multiple": true (Create/Update modules) or "type":"hidden" (Search/Get modules), 
		updated by: Kat Ben
	*/

	if (!input) return;
	if (typeof fields !== 'object') return

	function updateObj(obj, keys) {
		keys.forEach(i => {
			// check if it's not array then make it array
			// all raw multiple_choise field should be array due to interface mapping
			if (!Array.isArray(obj[i])) {
				obj[i] = [obj[i]];
			}
		});
		return obj;
	}

	let arrayfields = [];
	//New Watch module has only collection of parameters
	if (!Array.isArray(fields)) {
		arrayfields = Object.keys(fields)
			.filter(item => /^field_\d+?/.test(item))
			.map(item => item + '_raw');
	}

	//array of field keys/names that should be converted in old and new modules
	if (Array.isArray(fields)) {
		arrayfields = fields.filter(item => item.type == "multiple_choice" || item.multiple || item.type === 'hidden')
			.map(item => item.name ? item.name + '_raw' : item.key + '_raw');
	}

	const matches = Object.keys(input).filter(i => arrayfields.includes(i));
	return updateObj(input, matches);
}

