        "method": "GET",
        "qs": {
            "query": "{{parameters.email}}",
            "minimized": true
        },
        "body": {},
        "response": {
            "temp": {
                "subscriberExists": "{{length(body) > 0}}"
            },
            "output": null
        }
    },
    {
        "condition": "{{!temp.subscriberExists}}",
        "url": "/subscribers",
        "method": "POST",
        "body": "{{removeNull(parameters)}}",
        "response": {
            "type": {
                "200-300": "raw"
            },
            "output": "{{transformSubscriberOutput(convertLongNumToString(body))}}"
        }
    },
