function webhookResponse(obj) {

    if (!obj) return;

    else if (obj.subscriber) {
        let o = obj.subscriber
        for(let [key, value] of Object.entries(o)) {
            if(/^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}$/gm.test(value)) {
                o[key] = iml.parseDate(value);
            }
            else if (key === 'fields') {
                let fields = {};
                value.forEach(i => {
                    fields[i.key] = i.value;
                });
                o[key] = fields;
            }
        }
        if (obj.group) {
