function convertLongNumToString(str) {
	if(!str) return;

	let arr = str.match(/:(\d{15,}),/gm);

	if (arr) {
		arr = Array.from(new Set(arr));
		arr.forEach(item => {

			item = item.substring(1, item.length - 1);

			str = str.replace(new RegExp(item, 'gm'), '"' + item + '"');

			if(str.includes('""' + item + '""')) {
                str = str.replace(new RegExp('""' + item + '""', 'gm'), '"' + item + '"');
			}

		});
	}
	return JSON.parse(str);   
}

