function transformSubscriberOutput(subscriber) {
	if (!subscriber.fields || !subscriber.fields.length) return subscriber;

	const acc = {};
	for(let item of subscriber.fields) {
		acc[item.key] = item.value;
	}

	subscriber.fields = acc;
	return subscriber;
}
