function nameToLabel(str) {
    if (typeof str !== "string") return str;
    //author: KB
    // used in parseInterface, parseExpect and custom Record types for label

    // attribute names
    const abrv = ['Id', 'Url', 'Dff', 'Gdf', 'Dm', 'Uri'];
    const name = str
        // custom records type labels
        .replace(/_c$/, '').replace('_', ' ')
        // Capitalize the first character
        .replace(str.charAt(0), str.charAt(0).toUpperCase());
    const nameArr = name.split(/(?<=[a-z\d])(?=[A-Z])|(?<=[A-Z])(?=[A-Z][a-z])|(?<=[a-z])(?=[\d])/);

    return nameArr.map(word => {
        if (abrv.includes(word)) return word.toUpperCase();
        return word;
    }).join(' ');
}

