    if (!name.startsWith('Microsoft.NAV.')) return undefined;

    const typeName = name.replace('Microsoft.NAV.', '');
    const target = enumTypes.find((enumType) => enumType._attributes.Name === typeName);
    if (!target) return undefined;

    return target.Member
