function parseMetadata(metadata, type, entity) {
    if (!metadata || typeof metadata !== 'object') {
        throw new Error('Impossible to gather Resources information', 500);
    }

    const {
        EntityType: entityTypes,
        EnumType: enumTypes,
        ComplexType: complexTypes,
    } = metadata["edmx:Edmx"]?.["edmx:DataServices"]?.at(0)?.Schema?.at(0) || {};

    if (
        !Array.isArray(entityTypes) ||
        !Array.isArray(enumTypes) ||
        !Array.isArray(complexTypes)
    ) {
        throw new Error('Impossible to gather Resources information', 500);
    }

    const entityType = entityTypes.find((entityType) => entityType._attributes.Name === entity);
    if (!entityType) {
        throw new Error('Impossible to gather Resources information', 500);
    }

    const entityKey = entityType.Key?.at(0)?.PropertyRef?.at(0)?._attributes.Name || 'id';
    const typeMap = {
        "Edm.int64": "number",
        "Edm.Date": "date",
        "Edm.Guid": "uuid",
        "Edm.String": "text",
        "Edm.Boolean": "boolean",
        "Edm.Decimal": "number",
        "Edm.Int32": "number",
        "Edm.Stream": "buffer",
        "Edm.DateTimeOffset": "date"
    };

    const readonlyFields = [
        'lastModifiedDateTime'
    ];

    const fields = entityType.Property.reduce((array, entity) => {
        if (type === 'create' && entity._attributes.Name === entityKey) {
            return array;
        }

        if (type === 'update' && entity._attributes.Name === entityKey) {
            array.push('rpc://idFinder');
            return array;
        }

        const param = {
            name: entity._attributes.Name,
            type: typeMap[entity._attributes.Type],
            label: iml.nameToLabel(entity._attributes.Name)
        };

        const isUpsert = ['create', 'update'].includes(type);
        if (isUpsert) {
            if (readonlyFields.includes(entity._attributes.Name)) return array;
            param.required = (type === 'update' && entity._attributes.Name === entityKey);

            if (entity._attributes.MaxLength) {
                param.validate = { max: entity._attributes.MaxLength };
            }
        }

        const isEnumType = entity._attributes.Type.startsWith('Microsoft.NAV.');
        if (isEnumType) {
            param.type = isUpsert ? 'select' : 'text';
            if (isUpsert) {
                param.options = iml.findEnumType(enumTypes, entity._attributes.Type);
                param.dynamic = true;
            }

            array.push(param);
            return array;
        }

        const isComplexType = entity._attributes.Type.startsWith('Collection(Microsoft.NAV.');
        if (isComplexType) {
            const spec = iml.findComplexType(complexTypes, entity._attributes.Type, type);
            param.type = 'collection';
            param.spec = spec;

            array.push(param);
            return array;
        }

        array.push(param);
        return array;
    }, []);

    if (type === 'update') {
        fields.push({
            "name": "etag",
            "type": "text",
            "label": "OData eTag",
            "help": "Token to resolve update concurrency. If the eTag provided does not match the current eTag on the record, the record will not be updated.",
            "advanced": true
        });
    }

    return fields;
}

