function parseResponse(parameters) {
	if (typeof parameters !== 'object' || parameters === null) {
		return parameters;
	}

	if (Array.isArray(parameters)) {
		return parameters.map(item => iml.parseResponse(item));
	}

	let params = Object.assign({}, parameters);

	Object.entries(parameters).forEach(([key, value]) => {
		if (Array.isArray(value)) {
			params[key] = value.map(item => iml.parseResponse(item));
			return;
		}

		if (typeof value === 'object' && value !== null) {
			params[key] = iml.parseResponse(value);
			return;
		}

		const ISO_8601 = /^\d{4}(-\d\d(-\d\d(T\d\d:\d\d(:\d\d)?(\.\d+)?(([+-]\d\d:?\d\d)|Z)?)?)?)?$/i;
		const isNumeric = /^-?\d+$/;

		if (typeof value === 'string' && ISO_8601.test(value) && !isNumeric.test(value)) {
			params[key] = new Date(value);
			return;
		}

		const empty_uuid = /^00000000-0000-0000-0000-000000000000$/

		if (typeof value === 'string' && empty_uuid.test(value)) {
			delete params[key];
			return;
		}

	});

	return params;
}

