function prepareFilter(filter) {
    if (!Array.isArray(filter) || filter.length === 0) return undefined;
    return filter.map(criteria => (!criteria || !Array.isArray(criteria)) ? undefined : criteria.map(criterion => {
        const [dataType, operand] = criterion.o.split("::");
        switch (dataType) {
            case "text":
                if (operand === "contains") {
                    return `${criterion.a} eq '*${criterion.b}*'`;
                }

                if (operand === "startswith") {
                    return `${criterion.a} eq '${criterion.b}*'`;
                }

                if (operand === "endswith") {
                    return `${criterion.a} eq '*${criterion.b}'`;
                }

                return `${criterion.a} ${operand} '${criterion.b}'`;

            case "number":
                return `${criterion.a} ${operand} ${parseFloat(criterion.b)}`;

            case "date":
                return `${criterion.a} ${operand} ${new Date(criterion.b).toISOString()}`;

            case "bool":
                return `${criterion.a} ${operand} ${Boolean(criterion.b)}`;

            case "guid":
                return `${criterion.a} ${operand} ${criterion.b}`;
        }
    }).join(" and ")).join(" or ");
}

