function prepareOrderBy(fieldOrders) {
    if (typeof fieldOrders === 'string') return fieldOrders;
    if (!Array.isArray(fieldOrders) || fieldOrders.length === 0) return undefined;
    return fieldOrders
        .map((fieldOrder) => `${fieldOrder.field} ${fieldOrder.order}`)
        .join(',');
}

