function findComplexType(complexTypes, name, type) {
    if (!Array.isArray(complexTypes) || typeof name !== 'string') {
        throw new Error('Impossible to gather Resources information', 500);
    };

    if (!name.includes('Microsoft.NAV.')) return undefined;

    const typeName = name.replace('Collection(Microsoft.NAV.', '').replace(')', '');
    const target = complexTypes.find((complexType) => complexType._attributes.Name === typeName);
    if (!target) return undefined;

    const typeMap = {
        "Edm.int64": "number",
        "Edm.Date": "date",
        "Edm.Guid": "uuid",
        "Edm.String": "text",
        "Edm.Boolean": "boolean",
        "Edm.Decimal": "number",
        "Edm.Int32": "number",
        "Edm.Stream": "buffer",
        "Edm.DateTimeOffset": "date"
    }

    return target.Property.reduce((array, entity) => {
        const param = {
            name: entity._attributes.Name,
            type: typeMap[entity._attributes.Type],
            label: iml.nameToLabel(entity._attributes.Name)
        };

        const isUpsert = ['create', 'update'].includes(type);
        if (isUpsert) {
            if (entity._attributes.MaxLength) {
                param.validate = { max: entity._attributes.MaxLength };
            }
        }

        const isComplexType = entity._attributes.Type.startsWith('Collection(Microsoft.NAV.');
        if (isComplexType) {
            const spec = findComplexType(complexTypes, entity._attributes.Type, type);
            param.type = 'collection';
            param.spec = spec;

            array.push(param);
            return array;
        }

        array.push(param);
        return array;
    }, []);
}

findComplexType.test:
it('should throw error on invalid input', () => {
    assert.throws(() => { findComplexType() }, Error, 'Impossible to gather Resources information');
    assert.throws(() => { findComplexType(null) }, Error, 'Impossible to gather Resources information');
    assert.throws(() => { findComplexType(undefined) }, Error, 'Impossible to gather Resources information');
    assert.throws(() => { findComplexType([], null) }, Error, 'Impossible to gather Resources information');
    assert.throws(() => { findComplexType([], undefined) }, Error, 'Impossible to gather Resources information');
});

