      const formatPatterns = [
        // ISO,
        /^[0-9]+-([0][1-9]|[1][0-2])-([0][1-9]|[1-2][0-9]|[3][0-1])$/,
        // US
        /^([1-9]|[1][0-2])\/([1-9]|[1-2][0-9]|[3][0-1])\/[0-9]+$/,
        // European
        /^([0-2][0-9]|[3][0-1])\/([0][1-9]|[1][0-2])\/[0-9]+$/,
      ];

      let matchFound = false;

      formatPatterns.forEach((formatPattern) => {
        const match = date.match(formatPattern);

        if (match) {
          matchFound = true;
        }
      });

      return matchFound;
    }

    for (let [key, value] of Object.entries(obj)) {
      if (value) {
        if (
          typeof value === "string" &&
          value.length > 7 &&
          JSON.stringify(new Date(value)) !== "null" &&
          digitsAtEndValidator(value) &&
          !dateWithoutTime(value)
        ) {
          const formatDate = iml.formatDate(value, "MM-DD-YYYY HH:mm:ss");
          obj[key] = iml.parseDate(formatDate, "MM-DD-YYYY HH:mm:ss", "UTC");
        } else if (Array.isArray(value)) {
          obj[key] = value.map((item) =>
            iml.parseDates(item, "MM-DD-YYYY HH:mm:ss", "UTC")
          );
        } else if (typeof value === "object") {
          obj[key] = iml.parseDates(value, "MM-DD-YYYY HH:mm:ss", "UTC");
        }
      }
    }
    return obj;
  }

