	if (typeof body.error === 'object' && body.error !== null) {
		if (body.error.innererror) {
			if (body.error.innererror.internalexception) {
				return body.error.innererror.internalexception.message;
			}
			return body.error.innererror.message;
		}
		return body.error.message;
	} else {
		return body.error_description || body.Message;
	}
}

