function mergeCollections(target, ...sources) {
    const isCollection = (object) =>
        typeof object === 'object' &&
        object !== null &&
        !Array.isArray(object) &&
        !(object instanceof Date);

    if (!isCollection(target)) {
        throw new Error('mergeCollections: merge target is not a collection.');
    }

    // Deep clone for vm2 -> isolated-vm
    const output = JSON.parse(JSON.stringify(target));

    return sources.reduce((acc, source) => {
        if (!isCollection(source)) return acc;
        return Object.assign(acc, source);
    }, output);
}

