function filterEntityParameters(properties, requestType) {
	let props = [...properties];

	if (requestType === 'create') {
		return props.filter(prop => prop.IsValidForCreate)
			.sort((a, b) => a.SchemaName.localeCompare(b.SchemaName))
	}

	if (requestType === 'update') {
		return props.filter(prop => prop.IsValidForUpdate)
			.sort((a, b) => a.SchemaName.localeCompare(b.SchemaName))
	}

	if (requestType === 'interface') {
		return props.filter(prop => prop.IsValidForRead)
	}

	return props;
}

