function resolveParameterOptions(item, logicalName) {
    if (item.AttributeType === 'Picklist') {
		return `rpc://listPicklistAttributeMetadata?logicalName=${logicalName}&metricId=${item.MetadataId}`
	}
    if (item.AttributeType === 'Owner') {
		return `rpc://listLookupMetadata?logicalName=${item.Targets[0]}`
	}
	if (item.AttributeType === 'Lookup') {
		return item.Targets.map(target => {
            if(item.Targets.length === 1) {
                return {
                    "label": target,
                    "value": target,
                    "nested": [
                        {
                            "name": item['LogicalName'] + '@odata.bind',
                            "type": "select",
                            "options": `rpc://listLookupMetadata?logicalName=${target}`,
                            "label": iml.ifempty(item.DisplayName.LocalizedLabels[0].Label, item.SchemaName)
                        }
                    ]
                }
            } else {
                return {
                    "label": target,
                    "value": target,
                    "nested": [
                        {
                            "name": item['LogicalName'] + `_${target}@odata.bind`,
                            "type": "select",
                            "options": `rpc://listLookupMetadata?logicalName=${target}`,
                            "label": iml.ifempty(item.DisplayName.LocalizedLabels[0].Label, item.SchemaName)
                        }
                    ]
                }
            }
		})
	}
	return undefined;
}
