watchContacts.apiJsonc:
{
    "url": "/me{{if(parameters.contactFolder, '/contactfolders/' + parameters.contactFolder)}}/contacts",
    "method": "GET",
    "qs": {
        //"$search": "{{ifempty(parameters.search, undefined)}}",
        "$top": 500,
        "$orderby": "{{if(parameters.search, undefined, switch(parameters.select, 'create', 'createdDateTime desc', 'update', 'lastModifiedDateTime desc'))}}"
    },
    "response": {
        "iterate": "{{body.value}}",
        "trigger": {
            "id": "{{item.id}}",
            "date": "{{switch(parameters.select, 'create', item.createdDateTime, 'update', item.lastModifiedDateTime)}}",
            "type": "date",
            "order": "desc"
        },
        "output": "{{generalResponse(item)}}",
        "limit": "{{parameters.limit}}"
    },
    "pagination": {
        "url": "{{body.`@odata.nextLink`}}",
        "condition": "{{ifempty(body.`@odata.nextLink`, false)}}"
    }
}

