function getEnv(envs, envId) {
	if (!Array.isArray(envs)) return undefined;
	return typeof envId === "string" ? 
		envs.find(env => env.EnvironmentId === envId) : 
		envs.find(env => (env.EnvironmentId || "").startsWith("Default-"));
}

