encodeInput.code:
function encodeInput(body, flowInput, expect) {
	const inputSchema = body.properties.inputs.schema.properties;
	const flowInputExpect = expect.find(p => p.name === "flowInput");
	const payload = flowInputExpect.spec.reduce((acc, parameter) => {
		const paramSchema = inputSchema[parameter.name];
		const inputType = parameter.metadata ? parameter.metadata.PADType : paramSchema;

		switch (inputType) {
			case "string":
			case "String":
				acc[parameter.name] = flowInput[parameter.name] || paramSchema.value;
				break;

			case "number":
			case "Numeric":
				acc[parameter.name] = Number(flowInput[parameter.name] || paramSchema.value);
				break;

			case "boolean":
			case "Bool":
				if (typeof flowInput[parameter.name] === "boolean") {
					acc[parameter.name] = flowInput[parameter.name];
					break;
				}
				switch (paramSchema.value) {
					case "True":
						acc[parameter.name] = true;
						break;
					case "False":
						acc[parameter.name] = false;
						break;
				}
				break;

			case "object":
			case "CustomObject":
				if (Array.isArray(flowInput[parameter.name])) {
					acc[parameter.name] = iml.toCollection(flowInput[parameter.name], "key", "value");
					break;
				}
				acc[parameter.name] = JSON.parse(paramSchema.value);
				break;

			case "List":
				// list input is an array of text
				if (Array.isArray(flowInput[parameter.name])) {
					acc[parameter.name] = flowInput[parameter.name];
					break;
				}
				acc[parameter.name] = JSON.parse(paramSchema.value);
				break;

			case "DataTable":
				// table input is an array of array of collection
				if (Array.isArray(flowInput[parameter.name])) {
					acc[parameter.name] = flowInput[parameter.name].map(row => {
						return iml.toCollection(row, "key", "value");
					});
					break;
				}
				acc[parameter.name] = JSON.parse(paramSchema.value);
				break;

			default:
				acc[parameter.name] = flowInput[parameter.name] || paramSchema.value;
		}

		return acc;
	}, {});

	return JSON.stringify(payload);
}

