function getDesktopFlowSchema(obj, type, definition) {
	if (typeof obj !== "object" || obj === null) {
		throw new Error('Unknown schema.');
	}
	if (!["inputs", "outputs"].includes(type)) {
		throw new Error('Unknown output type.');
	}

	if (type === 'inputs') {
		const typeMap = {
			string: "text",
			number: "number",
			boolean: "boolean",
			object: "array",
			// clientdata cannot distinguish between list and datatable
			// array: "array",
			String: "text",
			Numeric: "number",
			Bool: "boolean",
			CustomObject: "array",
			List: "array",
			DataTable: "array"
		};
		const specMap = {
			object: {
				type: "collection",
				label: "Attribute",
				spec: [
					{
						name: "key",
						type: "text",
						label: "Key"
					},
					{
						name: "value",
						type: "any",
						label: "Value"
					}
				]
			},
			CustomObject: {
				type: "collection",
				label: "Attribute",
				spec: [
					{
						name: "key",
						type: "text",
						label: "Key"
					},
					{
						name: "value",
						type: "any",
						label: "Value"
					}
				]
			},
			List: {
				type: "text",
				label: "Item"
			},
			DataTable: {
				type: "array",
				label: "Row",
				spec: {
					type: "collection",
					label: "Column",
					spec: [
						{
							name: "key",
							type: "text",
							label: "Key"
						},
						{
							name: "value",
							type: "any",
							label: "Value"
						}
					]
				}
			}
		};
		const defaultMap = (v) => {
			switch (v.PADType) {
				case "string":
				case "String":
					return v.value;
				case "number":
				case "Numeric":
					return Number(v.value);
				case "boolean":
				case "Bool":
					if (v.value === "True") return true;
					if (v.value === "False") return false
					return undefined;
				case "object":
				case "CustomObject":
					// PAD's bug in JSON object
					// { 'Hello': "World" } <-- Single quote
					return iml.toArray(JSON.parse(v.value.replaceAll("'", "\"")));
				case "List":
					// PAD's bug in JSON list
					// ["a","b",] <-- ending comma
					return JSON.parse(v.value.replace(/,(?:\r|\n)*]/, "]"));
				case "DataTable":
					// table is an array of collection
					const table = JSON.parse(v.value);
					return table.map(row => {
						return Object.entries(row).map(([key, value]) => {
							return { key, value };
						});
					});
			}
		};

		const fields = Object.entries(obj)
			.map(([key, variable]) => {
				variable.PADType = definition ? definition[key] : variable.type;
				variable.default = defaultMap(variable);
				variable.type = typeMap[definition ? definition[key] : variable.type];
				variable.spec = specMap[definition ? definition[key] : variable.type];
				return [key, variable];
			})
			.filter(([key, variable]) => variable.type)
			.map(([key, variable]) => {
				const parameter = {
					name: key,
					type: variable.type,
					label: variable.title,
					help: variable.description,
					default: variable.default,
					metadata: {
						PADDefault: variable.value,
						PADType: variable.PADType
					}
				}
				if (variable.spec) {
					parameter.spec = variable.spec;
				}

				return parameter;
			});

		return {
			name: "flowInput",
			type: "collection",
			label: "Flow Input Variables",
			spec: fields
		};
	}

	if (type === 'outputs') {
		const typeMap = {
			string: "text",
			number: "number",
			boolean: "boolean",
			object: "collection",
			array: "array"
		};
		const specMap = {
			object: [],
			array: []
		};

		return Object.entries(obj)
			.filter(([key, variable]) => typeMap[variable.type])
			.map(([key, variable]) => {
				const interface = {
					name: key,
					type: "text",
					label: variable.title,
				};
				if (typeMap[variable.type]) {
					interface.type = typeMap[variable.type];
				}
				if (specMap[variable.type]) {
					interface.spec = specMap[variable.type];
				}

				return interface;
			});
	}
}

