function parseDates(obj) {
    const datePattern =
        /^\d{4}-(0[1-9]|1[012])-(0[1-9]|[12][0-9]|3[01])$/; // enhance it to match your use-case

    if (obj) {
        if (typeof obj === "object") {
            if (Array.isArray(obj)) {
                obj = obj.map((item) => iml.parseDates(item));
            } else {
                for (let [key, value] of Object.entries(obj)) {
                    if (value) {
                        if (
                            typeof value === "string" &&
                            value.match(datePattern)
                        ) {
                            obj[key] = iml.parseDate(
                                iml.formatDate(
                                    value,
                                    "YYYY-MM-DD"
                                )
                            );
                        } else if (Array.isArray(value)) {
                            obj[key] = value.map((item) =>
                                iml.parseDates(item)
                            );
                        } else if (typeof value === "object") {
                            obj[key] = iml.parseDates(value);
                        }
                    }
                }
            }
        }
        if (typeof obj === "string" && obj.match(datePattern)) {
            return iml.parseDate(
                iml.formatDate(obj, "YYYY-MM-DD")
            );
        }
    }

    return obj;
}

removeNull.code:
function removeNull(src) {
	if (src && typeof src === 'object') {
		Object.keys(src).forEach(key => {
			if (src[key] == null) {
				delete src[key];
			} else if (typeof src[key] === 'object') {
				iml.removeNull(src[key]);
			}
		});
	}
	return src;
}

