function formatOutput(output, metadata) {
	// Is used to transform the server response from Create Record back to Integromat types.
	if (!output || !metadata) return null;

	//format fixed fields
	output["_cd"] = new Date(output["_cd"] + '.000Z');
	output["_md"] = new Date(output["_md"] + '.000Z');
	//format record specific fields
	for (const key in output) {
		let metadataObject = metadata.find(elem => elem.name === key);

		if (metadataObject && metadataObject.meta && metadataObject.meta.type) { //if meta.type is empty we assume no transformation is required
			output[key] = iml.transformOutPutValue(output[key], metadataObject.meta.type);			
		}
	}

	return output;
}

