function prepareRequest(parameters, metadata) {
	if (!parameters || !metadata) return null;

	let params = iml.removeEmptyObjects(iml.removeEmptyArrays(iml.removeNull(parameters)));
	for (const key in params) {
		let metadataObject = metadata.find(elem => elem.name === key);

		if (metadataObject && metadataObject.meta && metadataObject.meta.type) { //if meta.type is empty we assume no transformation is required
			params[key] = iml.transformValue(params[key], metadataObject.meta.type);
		}
	}

	return params;
}

