function transformValue(value, ninoxType) {
	// Is used to transform the inputted module parameters to the suitable form that Ninox requires.
	if (value === null || !ninoxType) return; // value vs value === null (first also filters "", second does not)

	let newValue = value;

	switch (ninoxType) {
		case "appointment":
			// Appointments are passed through in the following way: start datetime of the appointment and number milliseconds specifying the duration separated by a '/' character.
			// E.g. 1557496437632/3600000 - This will create an appointment starting from 10 May 2019 - 01:53 P.M CEST that will last for an hour. 
			newValue = Date.parse(value.appointment_start) + '/' + (Date.parse(value.appointment_end) - Date.parse(value.appointment_start));
