function formatOutputAlt(output, metadata) {
    // Is used to transform the server response from Get Record, Look Up Record and Update Record back to Integromat types.
    if (!output || !metadata) return null;

    //format fixed fields
    output['createdAt'] = new Date(output['createdAt'] + '.000Z');
    output['modifiedAt'] = new Date(output['modifiedAt'] + '.000Z');
    iml.parseDates(output);
    iml.parseDates2(output);

    //format record specific fields
    new_fields_object = {};
    let metadataObject_fields = metadata.find((elem) => elem.name === 'fields')?.spec || [];
    for (const key in output.fields) {
        let metadataObject = metadataObject_fields.find((elem) => elem.label === key)

        if (metadataObject) {
            new_fields_object[metadataObject.name] = iml.transformOutPutValue(
                output.fields[key],
                metadataObject.meta.type,
            );
            delete output.fields[key];
        }
    }

    output.fields = new_fields_object;

    return output;
}

