it('should return null if output or metadata is missing', function () {
    assert.strictEqual(formatOutputAlt(null, []), null);
    assert.strictEqual(formatOutputAlt({}, null), null);
});

it('should format createdAt and modifiedAt fields correctly', function () {
    let output = {
        createdAt: '2024-02-04T12:00:00',
        modifiedAt: '2024-02-04T15:30:00',
        fields: {},
    };
    let metadata = [{ name: 'fields', spec: [] }];
    let result = formatOutputAlt(output, metadata);
    assert.strictEqual(result.createdAt instanceof Date, true);
    assert.strictEqual(result.modifiedAt instanceof Date, true);
});

it('should map field labels to metadata names', function () {
    let output = { fields: { 'Test Field': 'value' } };
    let metadata = [
        {
            name: 'fields',
            spec: [{ label: 'Test Field', name: 'testField', meta: { type: 'string' } }],
        },
    ];
    let result = formatOutputAlt(output, metadata);
    assert.strictEqual(result.fields.hasOwnProperty('Test Field'), false);
    assert.strictEqual(result.fields.hasOwnProperty('testField'), true);
});

