function convertNinoxField(field) {
	// Is used to generate the mappable parameters dynamically of Create and Update Record
	if (!field || !(field.type)) return;

	let obj = {};
	obj.name = field.id;
	obj.label = field.name;

	switch (field.type) {
		case "appointment":
			obj.type = "collection"; //consisting of a appointment_start and appointment_end

			let appointment_start = {};
			appointment_start.name = "appointment_start";
			appointment_start.type = "date";
			appointment_start.label = "Appointment Start";
			appointment_start.help = "Both start and end date need to be filled in. Only start or end date will not work.";

			let appointment_end = {};
			appointment_end.name = "appointment_end";
			appointment_end.type = "date";
			appointment_end.label = "Appointment End";
			appointment_end.help = "Both start and end date need to be filled in. Only start or end date will not work.";

			obj.spec = [appointment_start,appointment_end];

			obj.meta = {
				type: "appointment"
			};	
			break;
		case "boolean":
			obj.type = "boolean";
			break;
		case "choice":
			obj.type = "select";
			obj.options = (field.choices || []).map(option => ({label: option.caption, value: option.id}));

			obj.meta = {
				type: "choice"
			};
			break;
		case "color":
			obj.type = "color";
			obj.help = "Hex Colour Code, for example `#42A0FB`.";

			break;
		case "date":
			obj.type = "text";
			obj.help = "Please use 'YYYY-MM-DD' format.";
			obj.validate = {
                pattern: "^\\d{4}-(0[1-9]|1[012])-(0[1-9]|[12][0-9]|3[01])$"
            };
			obj.meta = {
				type: "date"
			};
			break;
		case "email":
			obj.type = "email";
			break;
		case "file":
			obj.type = "text";

			obj.meta = {
				type: "file"
			};
			break;
		case "html":
			obj.type = "text";
			obj.multiline = true;
			obj.meta = {
				type: "html"
			};
			break;
		case "icon":
			obj.type = "text";
			obj.help = "Icons are identified by their name, for example `info`.";
			obj.meta = {
				type: "icon"
			};
			break;
		case "link":
			obj.type = "url";
			obj.meta = {
				type: "link"
			};
			break;
		case "location":
			obj.type = "text";
			obj.label = "Location"; //bug in ninox, displays location name (e.g. Brisbane) as field name instead of Location
			obj.help = "Example: `Marienstrae 10, 10117 Berlin, Germany <52.52202224731445,13.38234806060791>`";
			obj.meta = {
				type: "location"
			};
			break;
		case "multi":
			obj.type = "text";
			obj.help = "The multiple choice field is not fully supported by their API currently. To select certain choices for the record from the multiple choice field. Enter a string consisting of the choices with ',' between them. For example: if the multiple choice field is 'Favourite pets' with options 'dog', 'cat', 'horse', 'snake', 'spider'. You can enter `dog, cat, horse` to select the choices 'dog', 'cat' and 'horse' from the multiple choices field for the record.";
			obj.meta = {
				type: "multi"
			};
			break;
		case "number":
			obj.type = "number";
			break;
		case "phone":
			obj.type = "text";
			obj.meta = {
				type: "phone"
			};
			break;			
		case "ref":
			obj.type = "uinteger";
			obj.help = "You can link this record (e.g. meeting record) back to a record from the main table (e.g. link a meeting record to a client record), to do that you need to give the ID of that record (e.g. client record) in this field.";
			obj.meta = {
				type: "ref"
			};
			break;
		case "string":
			obj.type = "text";

			obj.meta = {
				type: "string"
			};
			break;
		case "time":
			obj.type = "time";
			break;
		case "timeinterval":
			obj.type = "time";
			obj.meta = {
				type: "timeinterval"
			}
			break;
		case "timestamp":
			obj.type = "date";
			obj.meta = {
				type: "timestamp"
			};
			break;
		case "user":
			obj.type = "text";
			obj.help = "Users can be identified using a string. For example `XwDou29NsSAEoZHXF`";
			obj.meta = {
				type: "user"
			};
			break;
		default:
			return null;
	}

	return obj;
}

