function removeEmptyArrays(src) {
	if (src && typeof src === 'object') {
		Object.keys(src).forEach(key => {
			if (src[key] instanceof Array && src[key].length === 0) {
				delete src[key];
			} else if (typeof src[key] === 'object') {
				iml.removeEmptyArrays(src[key]);
			}
		});
	}
	return src;
}

