function getConnectionTeamID(privateURL) {
	//Example of an URL: `https://companyname.ninoxdb.de/teamID/api/v1` or `https://companyname.ninox.com/teamID/api/v1`
	//The URL is already regex tested during the connection creation so it should be OK
	if (privateURL.indexOf('.de/') !== -1) {
    	return privateURL.substring(privateURL.indexOf('.de/')+4,privateURL.indexOf('/api/v1'));
	} else { //then it is .com
		return privateURL.substring(privateURL.indexOf('.com/')+5,privateURL.indexOf('/api/v1'));
	}

}

