function transformOutPutValue(value, ninoxType) {
	// Is used to transform the server response from Create Record, Get Record, Look Up Record and Update Record back to Integromat types.
	if (value === null || !ninoxType) return;

	let newValue = value;

	switch (ninoxType) {
		case "appointment":
			newValue = {};
			newValue.type = "collection";

			let appointment_start = {};
			appointment_start.type = "date";
			let appointment_end = {};
			appointment_end.type = "date";

			two_dates_array = value.split(' - ');
			appointment_start = new Date(two_dates_array[0]+'Z') 
			appointment_end = new Date(two_dates_array[1]+'Z') 

			newValue = {appointment_start,appointment_end};

			break;
		case "boolean":
			// do nothing
			break;
		case "choice":
			// do nothing
			break;
		case "color":
			// do nothing
			break;
		case "date":
			// do nothing, regex validate happens in the generation of the field
			break;
		case "email":
			// do nothing
			break;
		case "file":
			// do nothing
			break;
		case "html":
			// do nothing
			break;
		case "icon":
			// do nothing
			break;
		case "link":
			// do nothing
			break;
		case "location":
			// do nothing
			break;
		case "multi":
			// do nothing
			break;
		case "number":
			// do nothing
			break;
		case "phone":
			// do nothing
			break;	
		case "ref":
			// do nothing
			break;
		case "string":
			// do nothing
			break;
		case "timeinterval":
			newValue = new Date(value).toISOString().slice(-13, -5)
			break;
		case "timestamp":
			newValue = new Date(value + '.000Z');
			break;
		case "time":
			// Do nothing
			break;
		case "user":
			// Do Nothing
			break;
		default:
			return;
	}

	return newValue;
}

