function getTableId(selectedTable) { 
	const [_tableName, _tableInternalName, tableId] = selectedTable.split('||'); 	
	return tableId;
}

parseRecord.code:
function parseRecord(data) {
	const keys = Object.keys(data);
	const parsedData = keys.reduce((acc, key) => {
		const val = data[key];
		let parsedVal = val;
		try {
			parsedVal = JSON.parse(val);
		} catch (err) {
			parsedVal = val;
		}
		acc[key] = parsedVal;
		return acc;
	}, {});
	return parsedData;
}

getInputSpec.code:
function getInputSpec(col) {
	const { uidt } = col;
	let spec;
	switch(uidt) {
		case "Attachment":
			spec = {
				name: "attachment",
				type: "collection",
				label: "Attachment",
				spec: [
					{
						"name": "url",
						"label": "URL",
						"type": "text"
					},
					{
						"name": "title",
						"label": "Title",
						"type": "text"
					},
					{
						"name": "mimetype",
						"label": "mimetype",
						"type": "text"
					},
					{
						"name": "size",
						"label": "size",
						"type": "uinteger"
					}
				]
			};
			break;
		default:
			spec = { "type": "text" };
	}
	return spec;
}

getInputType.code:
function getInputType(field) {
	const { dt, uidt, type } = field;
	let integromatType = "text";
	switch(true) {
		case [dt, uidt].includes("integer"):
			integromatType = "integer";
			break;
		case uidt === "Date":
		case uidt === "DateTime":
		case type === "timestamp":
			integromatType = "date";
			break;
		case uidt === "Attachment":
		case dt === "ARRAY":
			integromatType = "array";
			break;
		case dt === "boolean":
		case dt === "bool":
		case uidt === "Checkbox":
			integromatType = "boolean";
			break;
		default:
			integromatType = "text";
	}

	return integromatType;
}

getTableName.code:
function getTableName(selectedTable) {
	const [tableName, tableInternalName] = selectedTable.split('||');
	return tableName;
}

isFieldEditable.code:
function isFieldEditable(field) {
	// Autoincrement is not editable
	return !field.pk && !field.ai && field.uidt !== "LinkToAnotherRecord" ;
}

parseTableColumns.code:
function parseTableColumns(columns) {
	return columns.map((col) => {
		let type = iml.getInputType(col);
		const field = {
			name: col.title||col.column_name,
			label: col.title||col.column_name,
			type,
			editable: iml.isFieldEditable(col),
			creatable: iml.isFieldCreatable(col)
		};
		if (type === "array") {
			field.spec = iml.getInputSpec(col);
		}
		return field;
	});
}

formatRecordForSave.code:
function formatRecordForSave(record) {
	 const keys = Object.keys(record);
	 const newRecord = keys.reduce((acc, key) => {
		const val = record[key];
		if (Array.isArray(val)) {
			acc[key] = JSON.stringify(val);
		} else {
			acc[key] = val;
		}
		return acc;
	 }, {});
	 return newRecord;
}

verifyNonEmptyObject.code:
function verifyNonEmptyObject(obj) {
	return obj && Object.keys(obj).length === 0 && Object.getPrototypeOf(obj) === Object.prototype
}

getTablePrimaryKeyFromColumns.code:
function getTablePrimaryKeyFromColumns(columns) {
    const primaryKeyColumn = columns.find(col => col.pk);
	return [{
		"name": "id",
		"required": true,
		"label": primaryKeyColumn.title || primaryKeyColumn.column_name,
		"help": `Record ID (${primaryKeyColumn.title || primaryKeyColumn.column_name})`,
		"type": "text"
	},
	{
		"name": "primaryKeyColumnName",
		"default": primaryKeyColumn.title || primaryKeyColumn.column_name,
		"type": "hidden",
		"value": primaryKeyColumn.title || primaryKeyColumn.column_name
	}]
}
