function processInputFiles(title, content, files) {

  const stack = [];

  if (files && files.length > 0) {
    files.forEach((e, i) => {
      stack.push({
        key: "{" + ++i + "}",
        file: {
          value: e.value,
          options: {
            filename: e.fileName,
            contentType: iml.mime(e.fileName)
          }
        }
      });
    });
  }

  return {
    Presentation: {
      value: iml.pageSkeleton(iml.ifempty(title, ""), iml.ifempty(content, "")),
      options: {
        contentType: "text/html"
      }
    },
    ...iml.toCollection(stack, "key", "file")
  };
}

