parsePath.code:
function parsePath(parameters, describe) {
    // author KB
    // create url path for modules and RPCs, 'describe' is for interface

    const {recordTypeCategory, recordType, parentId} = parameters;
    let parent = recordTypeCategory.split(':')[1]

    //parent in module's url or RPC for parents
    if (recordType === parent || !parentId || describe) return parent; 

    // child recordType
    if(parentId) return `${parent}/${parentId}/child/${recordType}`;
}

sortByKey.code:
function sortByKey(key, direction = 'asc') {
    if (typeof key !== "string" || key === "") return undefined;

    switch (direction) {
        case "desc":
            return (a, b) => a[key] < b[key] ? 1 : a[key] > b[key] ? -1 : 0;
        default:
            return (a, b) => a[key] > b[key] ? 1 : a[key] < b[key] ? -1 : 0;
    }
}

userScope.code:
function userScope(body, api, supported = [], scope = ["standard", "custom"]) {
    // author: KB+Ness
    // parse resources from connection and api prefix

    const recordTypes = Object.keys(body);
    if (recordTypes.length === 0) {
        throw new Error("\nuserScope: Unable to get record types for the specified user.");
    }

    if (typeof api !== "string" || !api) {
        throw new Error("\nuserScope: 'api' is not a string.");
    }

    const isSupported = (key) => supported.length === 0 || supported.includes(key);
    const isLov = (key) => typeof key === "string" && /L(ov|OV)(V\d+)?$/.test(key);
    const isCustom = (key) => typeof key === "string" && /_c$/.test(key);

    return scope
        .flatMap(s => {
            switch (s) {
                case "standard":
                    return recordTypes.filter(key => isSupported(key) && !isLov(key));

                case "custom":
                    return recordTypes.filter(key => isCustom(key));

                case "lov":
                    return recordTypes.filter(key => isSupported(key) && isLov(key));
            }
        })
        .map(key => `${api}:${key}`);

}

