function parseExpect(resources, parameters) {
    //author: Ness, edit: KB


    let { attributes, item: { links }, children } = resources;
    if (!Array.isArray(attributes)) {
        throw new Error("parseExpect: Invalid metadata.");
    };

    const { recordTypeCategory, recordType, module } = parameters;
    const category = recordTypeCategory.split(':')[1];
    const isChild = category !== recordType;

    if(isChild) {
        attributes = children[recordType].attributes;
        links = children[recordType].item.links;
    };
    const recordId = {
        "name": "id",
        "type": "text",
        "label": "ID",
        "required": true,
        "help": "Different record types use different parameters as ID. When mapping this value, use the `@context.key` value."
    };
    const ParentId = {
        "name": "parentId",
        "type": "text",
        "label": "Parent ID",
        "required": true,
        "help": "Different record types use different parameters as ID. When mapping this value, use the `@context.key` value."
    };
    let IDs = [recordId];

    if (isChild) IDs = [ParentId, recordId]

    if (["get", "delete"].includes(module)) return IDs;

    // Search and Trigger modules
    if (["search", "trigger"].includes(module)) {
        if (isChild) return [ParentId, `rpc://searchFilters?module=${module}`];
        return `rpc://searchFilters?module=${module}`
    };

    const typeMap = {
        "string": "text",
        "long text": "text",
        "datetime": "date",
        "attachment": "text"
    };
    const multilineMap = {
        "long text": true
    };
   /*  const isRequired = (attr, module) => {
        // All "inputRequired" field is required in create 
        // some required parameters with allowChanges:"inCreate" are problematic (Sales>Accounts)
        if (module === "create" && attr.inputRequired && attr.allowChanges !== "inCreate") return true;
        return undefined;
    } */
    const getHelp = (attr) => {
        const helpMsgs = [];
        if (attr.format === "BigInt") {
            helpMsgs.push("This field only accepts integer.");
        }
        if (attr.format === "BigDecimal") {
            helpMsgs.push("This field only accepts decimal.");
        }
        if (attr?.annotations?.description) {
            helpMsgs.push(attr?.annotations?.description || attr?.properties?.TOOLTIP);
        }
        return helpMsgs.join("\n");
    };
    const isValidate = (attr) => {
        // In Framework 9, high precision numbers are treated as string
        // Check if user is sending numbers 
        if (attr.format === "BigInt") {
            return { pattern: "^\\d+$" };
        }
        if (attr.format === "BigDecimal") {
            return { pattern: "^\\d+(?:\\.\\d+)?$" };
        }
        return undefined;
    };
   /*  const getRPC = (attr) => {
        if (attr.lov?.childRef) {
            const { href } = links.find(link => link.name === attr.lov.childRef) ?? {};
            if (!href) return undefined;
            // If href contains {somePathParameters} then the RPC is not useful at all
            if (href.indexOf("{") > -1) return undefined;
            const [label] = attr.lov?.displayAttributes;
            if (!label) return undefined;
            // We have to encode the href and decode in the RPC or else it won't work
            return `rpc://listOfValues?href=${encodeURIComponent(href)}&label=${label}`;
        }
        return undefined;
    } */

    const output = attributes
        .filter(attr => {
            // allowChanges: ['always', 'never', 'inCreate']
            // Read only attributes are not needed in expect
            if (attr.allowChanges === "never") return false;
            // inCreate attribute is not needed in update
            if (module === "update" && attr.allowChanges !== "always") return false;
            // skip parameters type: object
            if (attr.type === "object") return false;
            return true;
        })
        .map(attr => ({
            name: attr.name,
            type: typeMap[attr.type] || attr.type,
            label: iml.nameToLabel(attr.name),
           // required: isRequired(attr, module),
            help: getHelp(attr),
            multiline: multilineMap[attr.type],
            validate: isValidate(attr)
            //options: getRPC(attr)
        }))
        .map(item => {
            if (item.options) {
                item.spec = { type: item.type };
                item.type = "select";
                if (module === "update") item.mode = "edit";
            }
            return item;
        })
        .sort(iml.sortByKey("label"));

    out = [
        ...output.filter(i => i.required),
        ...output.filter(i => !i.required)
    ];

    if (module === 'update') return [...IDs, ...out]; //  Update module
    if (module === 'create' && isChild) return [ParentId, ...out]; //  Create child module

    return out;  //  Create module parent
}

