encodePayload.code:
function encodePayload(input) {
    if (Array.isArray(input)) return input.map(item => encodePayload(item));
    if (typeof parameters !== "object" || parameters === null) return input;
    return Object.entries(input).reduce((acc, [key, value]) => {
        if (value instanceof Date) {
            acc[key] = iml.formatDate(value, 'YYYY-MM-DDTHH:mm:ss.SSS\\Z', 'UTC');
            return acc;
        }
        if (typeof value === "object" && value !== null) {
            acc[key] = encodePayload(value);
            return acc;
        }
        acc[key] = value;
        return;
    }, {});
}

parseChildren.code:
function parseChildren(resource, parameters) {
    // author: KB
    // get a list of parent and children availabla for specific module

    if (!resource) return;
    //return resource;

    function getActions(meta) {
        // authors: KB + Alex
        return meta.collection.actions
            .filter(item => ["get", "create", "update", "delete"].includes(item.name))
            .map(item => {
                item.name === 'get' ? item.name = 'search' : item.name;
                return item.name;
            })
            .concat(meta.item.actions
                .filter(item => ["get", "create", "update", "delete"].includes(item.name))
                .map(item => item.name))
    };

    let { recordTypeCategory, module } = parameters;
    if (module === 'trigger') module = 'search';
    const category = recordTypeCategory.split(':')[1];
    const parentActions = getActions(resource);

    if (!parentActions.includes(module)) return; // no user rights to this resource

    const parent = {
        value: category,
        label: iml.nameToLabel(category),
        default: true
    }

    if (!resource.children) return [parent];

    const children = Object.entries(resource.children)
        .map(([key, meta]) => ({
            name: key,
            actions: getActions(meta)
        }))
        .filter(item => item.actions.includes(module))
        .map(item => ({
            value: item.name,
            label: iml.nameToLabel(item.name)
        }));

    return [parent, ...children];
}

parseResponse.code:
function parseResponse(parameters) {
	if (typeof parameters !== 'object' || parameters === null) {
		return parameters;
	}

	if (Array.isArray(parameters)) {
		return parameters.map(item => iml.parseResponse(item));
	}

	let params = Object.assign({}, parameters);

	Object.entries(parameters).forEach(([key, value]) => {
		if (Array.isArray(value)) {
			params[key] = value.map(item => iml.parseResponse(item));
			return;
		}

		if (typeof value === 'object' && value !== null) {
			params[key] = iml.parseResponse(value);
			return;
		}

		const ISO_8601 = /^\d{4}(-\d\d(-\d\d(T\d\d:\d\d(:\d\d)?(\.\d+)?(([+-]\d\d:?\d\d)|Z)?)?)?)?$/i;
		const isNumeric = /^-?\d+$/;

		if (typeof value === 'string' && ISO_8601.test(value) && !isNumeric.test(value)) {
			params[key] = new Date(value.replace(/\s+/g, ''));
			return;
		}
	});

	return params;
}

