encodeSearchFilter.code:
function encodeSearchFilter(filter, custom) {
    if (custom) return custom;
    if (!Array.isArray(filter)) return undefined;
    const FilterError = (reason) => Error(`\nencodeSearchFilter: ${reason}`);
    const complFilter = filter.map(or => or.flatMap(and => {
        const { a, o, b } = and;
        if (!a) {
            return null;
        }
        const [fieldType, field] = a.split("::");
        const [opType, operator, ci] = o.split("::");

        if (fieldType !== opType) {
            throw FilterError(`Type '${fieldType}' for '${field}' does not match the operator type '${opType}'.`);
        }

        if (["IS NULL", "IS NOT NULL"].includes(operator)) {
            return [field, operator].join(" ");
        }
        if (b === undefined || b === null) {
            return null;
        }
        const criterion = [ci ? `UPPER(${field})` : field, operator];
        switch (opType) {
            case "string":
                switch (operator) {
                    case "IN":
                    case "NOT IN":
                        const items = (Array.isArray(b) ? b : b.split(",")).filter(i => !!i);
                        if (items.length === 0) throw FilterError(`At least one item is required for ${field} ${operator}.`);
                        criterion.push(`(${items.map(i => `'${i}'`).join(",")})`);
                        break;

                    default:
                        criterion.push(ci ? `UPPER('${b}')` : `'${b}'`);
                }
                break;

            case "boolean":
                if (typeof b === "boolean") criterion.push(b);
                else {
                    if (b === '') return null;
                    if (["true", "'true'", "Y", "'Y'"].includes(b)) {
                        criterion.push(true);
                        break;
                    }
                    if (["false", "'false'", "N", "'N'"].includes(b)) {
                        criterion.push(false);
                        break;
                    }
                    throw FilterError(`Invalid input '${b}' for ${field} ${operator}.`);
                }
                break;

            case "number":
            case "integer":
                if (b === '') return null;
                switch (operator) {
                    case "IN":
                    case "NOT IN":
                    case "BETWEEN":
                    case "NOT BETWEEN":
                        const items = (Array.isArray(b) ? b : b.split(",")).filter(i => !!i);
                        if (items.length === 0) throw FilterError(`At least one item is required for ${field} ${operator}.`);
                        if (items.some(i => isNaN(i))) throw FilterError(`Some item in '${b}' is not number for ${field} ${operator}.`);
                        if (["IN", "NOT IN"].includes(operator)) criterion.push(`(${items.join(",")})`);
                        if (["BETWEEN", "NOT BETWEEN"].includes(operator)) {
                            if (items.length !== 2) throw FilterError(`Exactly two items are required for ${field} ${operator}.`);
                            criterion.push(items.join(" AND "));
                        }
                        break;

                    default:
                        const item = Number(b);
                        if (isNaN(item)) throw FilterError(`Invalid input '${b}' for ${field} ${operator}.`);
                        criterion.push(item);
                }
                break;

            case "date":
            case "datetime":
                if (b === '') return null;
                switch (operator) {
                    case "BETWEEN":
                    case "NOT BETWEEN":
                        const items = (Array.isArray(b) ? b : b.split(",")).filter(i => !!i);
                        if (items.some(i => !iml.validDate(i))) throw FilterError(`Some item in '${b}' is not date for ${field} ${operator}.`);
                        if (items.length !== 2) throw FilterError(`Exactly two items are required for ${field} ${operator}.`);
                        criterion.push("'" + items.map(i => iml.formatDate(i, opType === "datetime" ? 'YYYY-MM-DDTHH:mm:ss.SSSZ' : 'YYYY-MM-DD') + "'").join(" AND "));
                        break;

                    default:
                        if (!iml.validDate(b)) throw FilterError(`Invalid input '${b}' for ${field} ${operator}.`);
                        criterion.push("'" + iml.formatDate(b, opType === "datetime" ? "YYYY-MM-DDTHH:mm:ss.SSSZ" : "YYYY-MM-DD") + "'");
                }
                break;

            default:
                criterion.push(b);
        }
        return criterion.join(" ");

    }).join(" AND ")).join(" OR ");

    return complFilter === '' ? null : complFilter;
}

