function getRecordTypeFields(fields, output) {
    //Author: Ness Li
    if (!Array.isArray(fields)) {
        throw new Error("\nfilterRecordTypeFields: 'fields' is not an array.");
    }
    switch (output) {
        case "filter":
            return fields
                .filter(field => field.queryable === true)
                .map(item => {
                    const type = ["BigInt", "BigDecimal"].includes(item.format) ? "number" : item.type;
                    return {
                        label: `${iml.nameToLabel(item.name)} [${iml.startcase(type)}]`,
                        value: `${type}::${item.name}`
                    };
                }).sort(iml.sortByKey("label"));

        case "order":
            return fields
                .filter(field => field.sortable !== false)
                .map(item => ({
                    label: iml.nameToLabel(item.name),
                    value: item.name
                })).sort(iml.sortByKey("label"));

        case "datetime":
            return fields
                .filter(field => field.type === 'datetime')
                .map(item => ({
                    label: iml.nameToLabel(item.name),
                    value: item.name
                })).sort(iml.sortByKey("label"));

        case "label":
            return fields
                .filter(item => ['date', 'string'].includes(item.type))
                .map(item => ({
                    label: iml.nameToLabel(item.name),
                    value: item.name
                })).sort(iml.sortByKey("label"));

        default:
            return fields
                .map(item => ({
                    label: iml.nameToLabel(item.name),
                    value: item.name
                })).sort(iml.sortByKey("label"));
    }
}

