function getSearchAttributes(body, parameters) {
    //author KB
    //returns attributes of parent or child for Search and Trigger module rpc SearchFilters

    if (!body) return;

    const { recordTypeCategory, recordType } = parameters;
    const category = recordTypeCategory.split(':')[1];
    const isChild = (category !== recordType);

    if (!isChild) return body.Resources[category].attributes;

    return body.Resources[category].children[recordType].attributes;

}

