filter.code:
function filter(array, key, ...values) {
	if(!Array.isArray(array)) return undefined;
	return array.filter(item => values.includes(item[key]));
}

parseMetadata.code:
function parseMetadata(metadata) {
	let m = Object.assign({}, metadata);

	const dateFields = [
		"created",
		"modified"
	];

	Object.keys(metadata).forEach(key => {
		if (dateFields.includes(key)) {
			m[key] = new Date(metadata[key]);
		}
	});

	return m;
}

decodeMediaLink.code:
function decodeMediaLink(body) {
	return {
		links: body.hosts.map(host => `https://${host}${body.path}`),
		expires: new Date(body.expires)
	};
}

encodePermission.code:
function encodePermission(permission) {
	let flag = 0;
	if (permission.create) flag += 1;
	if (permission.modify) flag += 2;
	if (permission.delete) flag += 4;
	return flag;
}

encodeUploadFileBody.code:
function encodeUploadFileBody(parameters) {
	return {
		path: parameters.path,
		renameifexists: parameters.overwrite ? undefined : 1,
		nopartial: 1,
		file: {
			value: parameters.data,
			options: {
				filename: parameters.filename
			}
		}
	};
}
