arrayToJson.code:
function arrayToJson(item) {
	if (item === undefined) {return {};}
    const payload = {};
    const parsed = item;
    for (i=0;i<parsed.length;i++){
        var name = parsed[i].name;
        var value = parsed[i].value;
        payload[name] = value;
    }
    return payload;
}

formatQuery.code:
function formatQuery(item) {
	debug(item);
	if (!item){ return undefined;}
    var query = '';

    for (var i=0, group_length=item.length; i<group_length; i++){
        var group = item[i];
        if (group.length > 1)
            query += '(';

        for(var j=0, length=group.length; j<length; j++){
            var condition = group[j];

            query += '(' + condition.a + ' ' + condition.o + ' ' + condition.b + ')';

            if (j < length - 1)
                query += ' AND ';
        }

        if (group.length > 1)
            query += ')';

        if (i < group_length - 1)
            query += ' OR ';
    }

    return query;
}

transformDate.code:
function transformDate(obj) {
	for (let [key, value] of Object.entries(obj)) {
		if (/^(19|20)\d\d-(0[1-9]|1[012])-([012]\d|3[01])T([01]\d|2[0-3]):([0-5]\d):([0-5]\d)$/.test(value)) {
			obj[key] = iml.parseDate(obj[key]);
		} else continue;
	}
	return obj;
}

dynamicInterface.code:
function dynamicInterface(data) {
	var arr = [];
	var type = "";
	for (var key in data){
		if (data[key] === null){
			data[key] = "";
		}

		if (typeof(data[key]) == "number"){
			type = "integer";
		}else{
			type = "text";
		}

		arr.push({"name": key, "type": type, "label": key});
	}

	return {
		name: "Data",
		label: "Data",
		type: "array",
		spec: arr
	};
}

