function buildCommentRequest(params) {
	let output = {};
	output.objectid = params.objectid;
	output.objecttypecode = params.objecttype;
	for (i=0;i<params.fields.length;i++){
        var name = params.fields[i].name;
        var value = params.fields[i].value;
        output[name] = value;
    }

	return output;
}

