function nameToLabel(str) {
    // author: Kat Ben

    if (typeof str !== "string") return str;
    const abbrv = ['id', 'url', 'uri', 'html', 'vat', 'zip', 'crm', 'bcws', 'bcwp', 'eac',
        'mdf','uuid','wf' ];
    const nameArr = str.includes(' ') ? str.split(' ') : str.split(/(?<=[a-z\d])(?=[A-Z])|(?<=[A-Z])(?=[A-Z][a-z])|(?<=[a-z])(?=[\d])|\s|_/);

    return nameArr.map((word, index) => {
        if (/^[A-Z]+$/gm.test(word)) return word //all caps word are considered abreviations 
        if (abbrv.includes(word.toLowerCase())) return word.toUpperCase(); // Capitalize abbreviations
        if (index === 0) return word.replace(word.charAt(0), word.charAt(0).toUpperCase()); // Capitalize the first character of first word
        return word.toLowerCase()
    }).join(' ');
}

