parseResponse.code:
function parseResponse(parameters) {
	// formats dates in API response
	//author: Simo and Kat

	//guards
	if (typeof parameters !== 'object' || parameters === null) return parameters;
	if(typeof parameters === 'object' && Object.keys(parameters).length === 0) return parameters;
	if (Array.isArray(parameters) && parameters.length === 0) return parameters;

	if (Array.isArray(parameters)) return parameters.map(item => iml.parseResponse(item));

	let params = Object.assign({}, parameters);

	Object.entries(parameters).forEach(([key, value]) => {
		if (Array.isArray(value)) {
			params[key] = value.map(item => iml.parseResponse(item));
			return;
		}

		if (typeof value === 'object' && value !== null) {
			params[key] = iml.parseResponse(value);
			return;
		}

        const TimestampOffset = /\/Date\((\-?[\d]{1,}[+][\d]{4})\)\//;
        const TimestampDate = /\/Date\((\-?[\d]{1,})\)\//;
        const manualDates = ['dateOfBirth'];

        //for some fields we want Date with no Time
         if ( value && manualDates.includes(key)) {
            date = value.match(TimestampDate)[1]
			params[key] = iml.formatDate(iml.parseDate(date+'+0000', 'x'), 'YYYY-MM-DD');
			return;
        } 

        //if the field contains timestamp and timezone, we parse it as date
		if (typeof value === 'string' && TimestampOffset.test(value)) {
            const date = value.match(TimestampOffset)[1]
			params[key] = iml.parseDate(date, 'x');
			return;
		}

        //if the field contains only the timestamp but no timezone, we keep it as raw data
		if (typeof value === 'string' && TimestampDate.test(value)) {
            const date = value.match(TimestampDate)[1]
			params[key] = date;
			return;
		}
	});

	return params;
}

