signAssertion.code:
function signAssertion(parameters, assertionID, sessionIndex) {
    //author: Ness
    const {
        UserID,
        Host,
        api_key,
        privateKey,
    } = parameters;

    const IssueInstant = iml.parseDate(Date.now(), 'x');
    const IssueInstantString = iml.formatDate(IssueInstant, 'YYYY-MM-DDTHH:mm:ss.SSS\\Z', 'UTC');
    const NotBeforeString = IssueInstantString;
    const NotOnOrAfter = iml.addMinutes(IssueInstant, 5);
    const NotOnOrAfterString = iml.formatDate(NotOnOrAfter, 'YYYY-MM-DDTHH:mm:ss.SSS\\Z', 'UTC');
    const AssertionID = assertionID;
    const SessionIndex = sessionIndex;
    const SignatureAlgorithm = "http://www.w3.org/2001/04/xmldsig-more#rsa-sha256";

    const AssertionFactory = (DTD = '', Signature = '') => `${DTD}<saml2:Assertion xmlns:saml2="urn:oasis:names:tc:SAML:2.0:assertion" xmlns:xs="http://www.w3.org/2001/XMLSchema" ID="${AssertionID}" IssueInstant="${IssueInstantString}" Version="2.0"><saml2:Issuer>www.successfactors.com</saml2:Issuer>${Signature}<saml2:Subject><saml2:NameID Format="urn:oasis:names:tc:SAML:1.1:nameid-format:unspecified">${UserID}</saml2:NameID><saml2:SubjectConfirmation Method="urn:oasis:names:tc:SAML:2.0:cm:bearer"><saml2:SubjectConfirmationData NotOnOrAfter="${NotOnOrAfterString}" Recipient="${Host}/oauth/token"></saml2:SubjectConfirmationData></saml2:SubjectConfirmation></saml2:Subject><saml2:Conditions NotBefore="${NotBeforeString}" NotOnOrAfter="${NotOnOrAfterString}"><saml2:AudienceRestriction><saml2:Audience>www.successfactors.com</saml2:Audience></saml2:AudienceRestriction></saml2:Conditions><saml2:AuthnStatement AuthnInstant="${IssueInstantString}" SessionIndex="${SessionIndex}"><saml2:AuthnContext><saml2:AuthnContextClassRef>urn:oasis:names:tc:SAML:2.0:ac:classes:PasswordProtectedTransport</saml2:AuthnContextClassRef></saml2:AuthnContext></saml2:AuthnStatement><saml2:AttributeStatement><saml2:Attribute Name="api_key"><saml2:AttributeValue xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xsi:type="xs:string">${api_key}</saml2:AttributeValue></saml2:Attribute></saml2:AttributeStatement></saml2:Assertion>`;
    const CanonicalizedAssertion = AssertionFactory();
    const Digest = iml.sha1(CanonicalizedAssertion, 'base64');
    const SignedInfo = `<ds:SignedInfo xmlns:ds="http://www.w3.org/2000/09/xmldsig#"><ds:CanonicalizationMethod Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"></ds:CanonicalizationMethod><ds:SignatureMethod Algorithm="${SignatureAlgorithm}"></ds:SignatureMethod><ds:Reference URI="#${AssertionID}"><ds:Transforms><ds:Transform Algorithm="http://www.w3.org/2000/09/xmldsig#enveloped-signature"></ds:Transform><ds:Transform Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"><ec:InclusiveNamespaces xmlns:ec="http://www.w3.org/2001/10/xml-exc-c14n#" PrefixList="xs"></ec:InclusiveNamespaces></ds:Transform></ds:Transforms><ds:DigestMethod Algorithm="http://www.w3.org/2000/09/xmldsig#sha1"></ds:DigestMethod><ds:DigestValue>${Digest}</ds:DigestValue></ds:Reference></ds:SignedInfo>`;

    // SignatureValue is just a placeholder for cryptoSign()
    // Once we migrate the cryptoSign() to imt-iml we can sign here
    // Not explicitly signing in the communication
    const SignatureValue = iml.sha256(SignedInfo, 'base64', privateKey);
    const Signature = `<ds:Signature xmlns:ds="http://www.w3.org/2000/09/xmldsig#"><ds:SignedInfo><ds:CanonicalizationMethod Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/><ds:SignatureMethod Algorithm="${SignatureAlgorithm}"/><ds:Reference URI="#${AssertionID}"><ds:Transforms><ds:Transform Algorithm="http://www.w3.org/2000/09/xmldsig#enveloped-signature"/><ds:Transform Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"><ec:InclusiveNamespaces xmlns:ec="http://www.w3.org/2001/10/xml-exc-c14n#" PrefixList="xs"/></ds:Transform></ds:Transforms><ds:DigestMethod Algorithm="http://www.w3.org/2000/09/xmldsig#sha1"/><ds:DigestValue>${Digest}</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue>${SignatureValue}</ds:SignatureValue></ds:Signature>`;

    const Assertion = AssertionFactory(`<?xml version="1.0" encoding="UTF-8"?>`, Signature);

    return {
        IssueInstant, 
        IssueInstantString,
        NotBeforeString,
        NotOnOrAfterString,
        CanonicalizedAssertion,
        Digest,
        SignedInfo,
        SignatureValue,
        Signature,
        Assertion,
    };
}

