function sortEndpoints(input, whitelist) {
    /*  
        Creates options for RecordType dropdown list from API response
        Splits endpoints to Verified and Beta sections based on whitelist, sorted alphabetically
        Adds '(Beta)' to beta options
        Used in RPC ListEndpoints where the whitelist temp variable is specified
        Author: Kat Ben
    */

    if (!Array.isArray(input) || input.length === 0) return undefined;
    if (!whitelist) throw new Error('Argument temp.whitelist must be present.');

    const verifiedArr = input.filter(item => whitelist.includes(item))
        .map(item => ({
            label: iml.nameToLabel(item),
            value: item
        }))
        .sort(iml.sortByKey("label"));

    const betaArr = input.filter(item => !whitelist.includes(item))
        .map(item => ({
            label: `${iml.nameToLabel(item)} (Beta)`,
            value: item
        }))
        .sort(iml.sortByKey("label"));

    const output = [];

    if (verifiedArr.length > 0) output.push({
        label: "Verified",
        options: verifiedArr
    });

    if (betaArr.length > 0) output.push({
        label: "Beta",
        options: betaArr
    });

    return output;
}

