function buildGetParameters(obj) {
    // creates a string from collection of mappable parameters
    // used in Get module communication
    // author: Simo + Kat

    if (!obj || typeof obj !== 'object' || Array.isArray(obj) || Object.keys(obj).length === 0) throw new Error ('Parameters cannot be empty.');
    const exclude = ["__IMTCONN__","__IMTHOOK__"] //these get passed in the parameters

    return Object.entries(obj)
    .filter(([key,value]) => !exclude.includes(key))
    .reduce((acc, [key,value]) => {
        acc.push(`${key}='${value}'`)
        return acc
    }, []).join(',');
}

