function parseMetadataToSelect(input, filter) {
    //parses response from metadata endpoint with nested structure
    // argument filter is used to filter viewable or sortable parameters
    // used in RPC Parameters: Select output and Parameters: Order by
    // author: Kat Ben

    if (!Array.isArray(input) || input.length === 0) return undefined
    if (!['sortable', 'viewable'].includes(filter)) throw new Error('Argument "filter" must be "viewable" or "sortable".')

    // sortable droplist has each option doubled (for asc and desc)
    if (filter === 'sortable') {
        return input.filter(item => item.sortable)
            .flatMap(item => ([{
                value: `${item.name} asc`,
                label: `${iml.nameToLabel(item.name)} [ascending]`
            },
            {
                value: `${item.name} desc`,
                label: `${iml.nameToLabel(item.name)} [descending]`
            }]))
            .sort(iml.sortByKey("label"));
    };

    // viewable droplist options
    return input.filter(item => item.viewable)
        .map(item => ({
            value: item.name,
            label: iml.nameToLabel(item.name)
        }))
        .sort(iml.sortByKey("label"));
}

