function parseMetadataToInterface(input) {
    //parses response from dynmicly fetched metadata from nonNavigationProperties and navigationProperties
    // used in RPC Interface:All
    // author: Kat Ben

    if (!input || typeof input !== 'object' || Array.isArray(input)) return []

    const { navigationProperties, nonNavigationProperties } = input;

    function mapType(typeName) {
        switch (typeName) {
            case "bool":
                return "boolean";
            case "datetime":
            case "string":
            case "time":
                return "text";
            case "Decimal":
            case "Double":
            case "Single":
            case "long":
            case "Int16":
            case "Int32":
            case "Int64":
            case "Byte":
            case "SByte":
                return "number";
            case "Guid":
            case "uuid":
                return "uuid";
            case "datetimeoffset":
                return "date";
            default:
                return "text";
        }
    };

    let regularProperties = [];
    let navProperties = [];

    if (nonNavigationProperties?.results) {
        regularProperties = nonNavigationProperties.results
            .filter(item => item.viewable)
            .map(item => ({
                name: item.name,
                label: iml.nameToLabel(item.name),
                type: mapType(item.type.name),
            }));
    }

    if (navigationProperties?.results) {
        navProperties = navigationProperties?.results
            .filter(item => item.viewable)
            .map(item => ({
                name: item.name,
                label: iml.nameToLabel(item.name),
                type: "collection",
            }));
    }

    return [...regularProperties, ...navProperties]
}

